import React, { useState } from 'react'
import './App.css'

function DisplayDate() {
    const [step,setStep]=useState(0);

    const handleChange = (val) =>{
        // alert(val);
        setStep(val);
    }

  return (
    <div className='container'>
        <div className='step-container'>
            <input className='range-input' type='range' min={0} max={30} onChange={(e)=>handleChange(e.target.value)}/>
            <p className='step-label'>step: {step} </p>
        </div>

        <div className='counter-container'>
        <input className='btn' value='-' onClick={handleDecrement}/>
        <input className='btn' value='' onClick={handleIncrement}/>
        </div>
    </div>
  )
}

export default DisplayDate
