    <div className='card-grid'>
        {quizQuestions.map((quest)=> 
            (
                <div className='card' onClick={() => handleAnswer(quest.id)}>
                    {selectId===quest.id ? quest.answer : quest.question}
                </div>
            )
        )}
    </div>
  )
