      const res = await fetch('https://fakestoreapi.com/products') //recuperer une liste de produit en utilisant
    } catch (error) {
      console.log('failed to fetch the product', error);
    }
  }

  return (
    <div className='product-container'>
      <h1> Product Title</h1>
      <img src='' alt='' />
      <p>You have seen 1 Product</p>
      <button>Generate Random Product</button>
    </div>
  )
}

export default Random
