import React, { useState } from 'react'
import './App.css'

function ExpenseTracker() {

    const [total,setTotal]=useState(0.01)
    const [expenseList,setExpenseList]=useState([
        {
            id,expense,amount
        }
    ])

    const handleSubmit = (e) => {
        e.preventDefault()
        // console.log(new FormData(e.target))
        const expense = new FormData(e.target)
        // console.log("Expense = ", expense.get('Expense'))
        // console.log("Amount = ", expense.get('Amount'))
        const tab = [expense.get('Expense') , expense.get('Amount')]
        const expenseListcopy = [...expenseList]
        expenseListcopy.push(tab)
        setExpenseList(expenseListcopy)
    }

    return (
        <div className='container'>
            <h1 className='header'>Expense Tracker</h1>

            <form className='inputContainer' onSubmit={(e) => handleSubmit(e)}>
                <input type='text' name='Expense' className='input' placeholder='Expense'></input>
                <input type='text' name='Amount' className='input' placeholder='Amount'></input>
                <input type='submit' value='Add' className='input button' />
            </form>

            <div className='expenseList'>
                <div className='expenseItem'>
                    <span>Rent</span>
                    <span>xaf 5000.00</span>
                    <span className='deleteButton'>Delete</span>
                </div>
            </div>

            <div className='total'>
                Total: xaf {total}
            </div>
        </div>
    )
}

export default ExpenseTracker
